import {useNavigate,useLocation} from "react-router-dom";
import { useEffect,useState,useRef } from "react";
let count = 1;
let hasIncremented = false;
function Home({count, setCount}) {
    const location = useLocation();
    const navigate = useNavigate();
    const pages = ["Contact","About"];

    // useEffect(() => {
    //    if (!hasIncremented) {
    //         count++;
    //         hasIncremented = true;
    //     }else{
    //        hasIncremented = false;
    //     }

    // }, []);\
    useEffect(() => {
      setCount(count++);
      console.log(count);
    }, [location]);

  return (  
    <div className="Home">
        <h1>HOME PAGE</h1>
        <div className="nav" style={{display:"flex"}}>
             {pages.map(el=><p key={el} style={{marginRight:"10px",cursor:"pointer" }} onClick={()=>navigate(`/${el.toLocaleLowerCase()}}`,{state:{count:count}})}>{el}</p>)}
        </div>
        <p>Banner: B00934148</p>
        <p>Count: {`${count}`}</p>

    </div>
  );
}

export default Home;
