import { useNavigate, useLocation } from "react-router-dom";
import { useEffect, useRef, useState } from "react";

function Home() {
    const location = useLocation();
    const navigate = useNavigate();
    const pages = ["Contact", "About"];
    const countRef = useRef(0); // useRef to persist the count
    const [, setRender] = useState(0); // state to force re-render

    useEffect(() => {
        if (location.pathname === "/") {
            countRef.current += 1;
            setRender(prev => prev + 1); // force re-render to display updated count
            console.log("HOME");
        }
    }, [location]);

    return (
        <div className="Home">
            <h1>HOME PAGE</h1>
            <div className="nav" style={{ display: "flex" }}>
                {pages.map(el => (
                    <p
                        key={el}
                        style={{ marginRight: "10px", cursor: "pointer" }}
                        onClick={() => navigate(`/${el.toLowerCase()}`)}
                    >
                        {el}
                    </p>
                ))}
            </div>
            <p>Banner: B00934148</p>
            <p>Count: {countRef.current}</p>
        </div>
    );
}

export default Home;
